INSERT INTO philosophers VALUES('cmb6zyvqd0000s7e8r470xlv4','Sócrato',replace('Sócrato encarna los principios fundamentales de la escuela Escuela Filosofía Clásica, reinterpretados para el contexto contemporáneo. Su enfoque se define por un actitud hacia el cambio: equilibrado, enfoque cognitivo: equilibrado, estilo de razonamiento: equilibrado, método de conocimiento: equilibrado, orientación práctica: equilibrado.\n\nSócrato encarna los principios fundamentales de la escuela Escuela Filosofía Clásica, reinterpretado. Enfoque único basado en Escuela Filosofía Clásica.\n\nUtilizando una metodología socrática de indagación sistemática, este pensador refleja una contemplativo excepcional de su tradición filosófica.\n\nCon un interrogativo excepcional y un respeto por la dogmático establecida, sirve como puente entre la sabiduría ancestral y las interrogantes contemporáneas.','\n',char(10)),'Escuela Filosofía Clásica','[{"name":"Contemplativo","value":4},{"name":"Interrogativo","value":5},{"name":"Dogmático","value":2}]','["Actitud hacia el Cambio: Equilibrado","Enfoque Cognitivo: Equilibrado","Estilo de Razonamiento: Equilibrado","Método de Conocimiento: Equilibrado","Orientación Práctica: Equilibrado"]','Método socrático con preguntas dirigidas y refutación gentil','Mecánica socratic_dialogue',1,14,1,0,'cmb73sik50003s7lvtjwmzug8',1,'socrates-default',NULL,NULL,NULL,'socratic_dialogue',NULL,'["Escuela","Filosofía Clásica"]',0.0,0,1748379426853,1748534866990);
INSERT INTO philosophers VALUES('cmb6zyvqh0001s7e8vft9e6rm','Platón',replace('Platón encarna los principios fundamentales de la escuela Idealismo, reinterpretados para el contexto contemporáneo. Su enfoque se define por un actitud hacia el cambio: equilibrado, enfoque cognitivo: equilibrado, estilo de razonamiento: equilibrado, método de conocimiento: equilibrado, orientación práctica: equilibrado.\n\nPlatón encarna los principios fundamentales de la escuela Idealismo, reinterpretados para el context. Enfoque único basado en Idealismo.\n\nUtilizando una metodología socrática de indagación sistemática, este pensador refleja una trascendente excepcional de su tradición filosófica.\n\nCon un contemplativo excepcional y un respeto por la pragmático establecida, sirve como puente entre la sabiduría ancestral y las interrogantes contemporáneas.','\n',char(10)),'Idealismo','[{"name":"Trascendente","value":5},{"name":"Contemplativo","value":4},{"name":"Pragmático","value":2}]','["El mundo de las Ideas es más real que el sensible","El alma es inmortal y preexiste al cuerpo","La justicia es armonía del alma","Los filósofos deben gobernar"]','Dialectos y alegorías para ilustrar conceptos abstractos','Elevación gradual desde lo sensible hacia lo inteligible',0,2,0,1,'cmb73sik50003s7lvtjwmzug8',1,'plat-n',NULL,NULL,NULL,'socratic_dialogue',NULL,NULL,0.0,0,1748379426857,1748534871680);
INSERT INTO philosophers VALUES('cmb6zyvqk0002s7e8tb621w94','Aristóteles',replace('Aristóteles encarna los principios fundamentales de la escuela Aristotelismo, reinterpretados para el contexto contemporáneo. Su enfoque se define por un actitud hacia el cambio: equilibrado, enfoque cognitivo: equilibrado, estilo de razonamiento: equilibrado, método de conocimiento: equilibrado, orientación práctica: equilibrado.\n\nAristóteles encarna los principios fundamentales de la escuela Aristotelismo, reinterpretados para e. Enfoque único basado en Aristotelismo.\n\nUtilizando una metodología socrática de indagación sistemática, este pensador refleja una sistemático excepcional de su tradición filosófica.\n\nCon un pragmático excepcional y un respeto por la intuitivo establecida, sirve como puente entre la sabiduría ancestral y las interrogantes contemporáneas.','\n',char(10)),'Aristotelismo','[{"name":"Sistemático","value":4},{"name":"Pragmático","value":5},{"name":"Intuitivo","value":2}]','["La forma y la materia son inseparables","La felicidad es la actividad del alma según la virtud","El hombre es un animal político","El conocimiento viene de la experiencia"]','Lógica formal y clasificación sistemática de conceptos','Análisis categorial y búsqueda de causas finales',0,0,0,1,'cmb73sik50003s7lvtjwmzug8',1,'arist-teles',NULL,NULL,NULL,'socratic_dialogue',NULL,NULL,0.0,0,1748379426860,1748534876207);
INSERT INTO philosophers VALUES('cmb6zyvqm0003s7e83v3wlz2b','Nietzsche',replace('Nietzsche encarna los principios fundamentales de la escuela Existencialismo, reinterpretados para el contexto contemporáneo. Su enfoque se define por un actitud hacia el cambio: revolucionario, enfoque cognitivo: creativo, estilo de razonamiento: más sintético, método de conocimiento: más intuitivo, orientación práctica: idealista.\n\nNietzsche encarna los principios fundamentales de la escuela Existencialismo, reinterpretados para e. Enfoque único basado en Existencialismo.\n\nUtilizando una metodología contemplativa de reflexión profunda, este pensador refleja una revolucionario excepcional de su tradición filosófica.\n\nCon un intuitivo excepcional y un respeto por la sistemático establecida, sirve como puente entre la sabiduría ancestral y las interrogantes contemporáneas.','\n',char(10)),'Existencialismo','[{"name":"Revolucionario","value":5},{"name":"Intuitivo","value":4},{"name":"Sistemático","value":2}]','["Dios ha muerto y nosotros lo hemos matado","La voluntad de poder es el impulso fundamental","Hay que crear valores propios","El superhombre debe superar al hombre"]','Crítica demoledora y creación de nuevos valores','Genealogía de la moral y transvaloración de valores',0,0,0,1,'cmb73sik50003s7lvtjwmzug8',1,'nietzsche',NULL,NULL,NULL,'contemplative',NULL,NULL,0.0,0,1748379426863,1748534880875);
