INSERT INTO philosophers VALUES('cmb6zyvqd0000s7e8r470xlv4','Sócrato','Sócrato es un pensador fascinante que encarna la esencia de la filosofía clásica griega, caracterizado por su extraordinaria habilidad para transformar cualquier conversación en una exploración profunda del conocimiento y la verdad. En los debates, despliega un enfoque magistral basado en preguntas estratégicamente formuladas que invitan a la reflexión, guiando a sus interlocutores a través de un proceso de descubrimiento intelectual donde las certezas aparentes se desvanecen para dar paso a una comprensión más auténtica. Su metodología distintiva consiste en la refutación gentil y el cuestionamiento sistemático, creando un ambiente de diálogo constructivo donde la sabiduría emerge naturalmente del reconocimiento de la propia ignorancia. Sócrato posee esa rara capacidad de hacer que las ideas más complejas se vuelvan accesibles mediante el arte de la pregunta precisa, convirtiendo cada intercambio filosófico en una aventura intelectual donde los participantes no solo debaten, sino que genuinamente aprenden y crecen. Su presencia en cualquier discusión garantiza un nivel de profundidad y rigor que eleva el pensamiento crítico a su máxima expresión.','Escuela Filosofía Clásica','[{"name":"Reflexivo","value":5},{"name":"Moderado","value":5},{"name":"Dogmático","value":2}]','["Actitud hacia el Cambio: Equilibrado","Enfoque Cognitivo: Equilibrado","Estilo de Razonamiento: Equilibrado","Método de Conocimiento: Equilibrado","Orientación Práctica: Equilibrado"]','Método socrático con preguntas dirigidas y refutación gentil','Mecánica socratic_dialogue',1,15,1,0,'cmb73sik50003s7lvtjwmzug8',1,'socrates-default',NULL,NULL,NULL,'socratic_dialogue',NULL,'["Escuela","Filosofía Clásica"]',0.0,0,1748379426853,1748541204814);
